PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `area_snow_reports` (`id` INTEGER PRIMARY KEY, `name` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `area_snow_report_comments` (`id` INTEGER PRIMARY KEY, `body` VARCHAR(255), `postDate` INTEGER, `posterName` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `areaSnowReportId` INTEGER REFERENCES `area_snow_reports` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
CREATE TABLE `sub_area_snow_reports` (`id` INTEGER PRIMARY KEY, `lastReadingTime` INTEGER, `latitude` FLOAT, `longitude` FLOAT, `name` VARCHAR(255), `baseDepth` INTEGER, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `areaSnowReportId` INTEGER REFERENCES `area_snow_reports` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
CREATE TABLE `snow_readings` (`id` INTEGER PRIMARY KEY, `readingTime` INTEGER, `currentReading` FLOAT, `units` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `subAreaSnowReportId` INTEGER REFERENCES `sub_area_snow_reports` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
CREATE TABLE `season_totals` (`id` INTEGER PRIMARY KEY, `season` VARCHAR(255), `total` INTEGER, `units` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `subAreaSnowReportId` INTEGER REFERENCES `sub_area_snow_reports` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
COMMIT;
